func testContentToManyReviews(t *testing.T) {
	var err error
	ctx := context.Background()
	tx := MustTx(boil.BeginTx(ctx, nil))
	defer func() { _ = tx.Rollback() }()

	var a Content
	var b, c Review

	seed := randomize.NewSeed()
	if err = randomize.Struct(seed, &a, contentDBTypes, true, contentColumnsWithDefault...); err != nil {
		t.Errorf("Unable to randomize Content struct: %s", err)
	}

	if err := a.Insert(ctx, tx, boil.Infer()); err != nil {
		t.Fatal(err)
	}

	if err = randomize.Struct(seed, &b, reviewDBTypes, false, reviewColumnsWithDefault...); err != nil {
		t.Fatal(err)
	}
	if err = randomize.Struct(seed, &c, reviewDBTypes, false, reviewColumnsWithDefault...); err != nil {
		t.Fatal(err)
	}

	b.ContentID = a.ID
	c.ContentID = a.ID

	if err = b.Insert(ctx, tx, boil.Infer()); err != nil {
		t.Fatal(err)
	}
	if err = c.Insert(ctx, tx, boil.Infer()); err != nil {
		t.Fatal(err)
	}

	check, err := a.Reviews().All(ctx, tx)
	if err != nil {
		t.Fatal(err)
	}

	bFound, cFound := false, false
	for _, v := range check {
		if v.ContentID == b.ContentID {
			bFound = true
		}
		if v.ContentID == c.ContentID {
			cFound = true
		}
	}

	if !bFound {
		t.Error("expected to find b")
	}
	if !cFound {
		t.Error("expected to find c")
	}

	slice := ContentSlice{&a}
	if err = a.L.LoadReviews(ctx, tx, false, (*[]*Content)(&slice), nil); err != nil {
		t.Fatal(err)
	}
	if got := len(a.R.Reviews); got != 2 {
		t.Error("number of eager loaded records wrong, got:", got)
	}

	a.R.Reviews = nil
	if err = a.L.LoadReviews(ctx, tx, true, &a, nil); err != nil {
		t.Fatal(err)
	}
	if got := len(a.R.Reviews); got != 2 {
		t.Error("number of eager loaded records wrong, got:", got)
	}

	if t.Failed() {
		t.Logf("%#v", check)
	}
}

func testContentToManyAddOpReviews(t *testing.T) {
	var err error

	ctx := context.Background()
	tx := MustTx(boil.BeginTx(ctx, nil))
	defer func() { _ = tx.Rollback() }()

	var a Content
	var b, c, d, e Review

	seed := randomize.NewSeed()
	if err = randomize.Struct(seed, &a, contentDBTypes, false, strmangle.SetComplement(contentPrimaryKeyColumns, contentColumnsWithoutDefault)...); err != nil {
		t.Fatal(err)
	}
	foreigners := []*Review{&b, &c, &d, &e}
	for _, x := range foreigners {
		if err = randomize.Struct(seed, x, reviewDBTypes, false, strmangle.SetComplement(reviewPrimaryKeyColumns, reviewColumnsWithoutDefault)...); err != nil {
			t.Fatal(err)
		}
	}

	if err := a.Insert(ctx, tx, boil.Infer()); err != nil {
		t.Fatal(err)
	}
	if err = b.Insert(ctx, tx, boil.Infer()); err != nil {
		t.Fatal(err)
	}
	if err = c.Insert(ctx, tx, boil.Infer()); err != nil {
		t.Fatal(err)
	}

	foreignersSplitByInsertion := [][]*Review{
		{&b, &c},
		{&d, &e},
	}

	for i, x := range foreignersSplitByInsertion {
		err = a.AddReviews(ctx, tx, i != 0, x...)
		if err != nil {
			t.Fatal(err)
		}

		first := x[0]
		second := x[1]

		if a.ID != first.ContentID {
			t.Error("foreign key was wrong value", a.ID, first.ContentID)
		}
		if a.ID != second.ContentID {
			t.Error("foreign key was wrong value", a.ID, second.ContentID)
		}

		if first.R.Content != &a {
			t.Error("relationship was not added properly to the foreign slice")
		}
		if second.R.Content != &a {
			t.Error("relationship was not added properly to the foreign slice")
		}

		if a.R.Reviews[i*2] != first {
			t.Error("relationship struct slice not set to correct value")
		}
		if a.R.Reviews[i*2+1] != second {
			t.Error("relationship struct slice not set to correct value")
		}

		count, err := a.Reviews().Count(ctx, tx)
		if err != nil {
			t.Fatal(err)
		}
		if want := int64((i + 1) * 2); count != want {
			t.Error("want", want, "got", count)
		}
	}
}

